CREATE TABLE IF NOT EXISTS `?:eldico_bridge_features` (`id` INT NOT NULL AUTO_INCREMENT , `eldc_id` INT NOT
NULL , `eldc_product_id` INT NOT NULL , `eldc_code` VARCHAR(55) NULL DEFAULT NULL , `eldc_feature_name`
VARCHAR(255) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB CHARSET=utf8mb4 COLLATE utf8mb4_general_ci;
